import Enumerable from 'linq'
import path from 'path'

import * as io from '../../util/io'
import { config } from './config'

const getApi = async <T>(type: string): Promise<ApiResult<T>> => {
  if (!config.allowed.includes(type)) {
    throw new Error(`Invalid type ${type}`)
  }

  try {
    const file = path.join(config.root, `${type}.json`)
    const contents = await io.readFile(file)
    const result = JSON.parse(contents)
    return result
  } catch (error) {
    console.error(error)
  }

  const result: ApiResult<T> = {
    docs: [] as T[],
    totalDocs: 0,
    limit: 0,
    totalPages: 0,
    page: 0,
    pagingCounter: 0,
    hasPrevPage: false,
    hasNextPage: false,
    prevPage: null,
    nextPage: null
  }

  return result
}

const getEnumerable = async <T>(type: string, limit: number, skip: number): Promise<T[]> => {
  try {
    const result = await getApi<T>(type)

    let enumerable = Enumerable.from(result.docs)

    if (limit > 0) {
      enumerable = enumerable.take(limit)
    }

    if (skip > 0) {
      enumerable = enumerable.skip(skip)
    }

    const enumerated = enumerable.toArray()

    return enumerated
  } catch (error) {
    console.error(error)
  }

  return []
}

const getPaginated = async<T>(type: string, pageSize: number = 12): Promise<PaginatedResult<T>> => {
  try {
    const { docs } = await getApi<T>(type)

    const records = docs.length
    const totalPages = Math.ceil(records / pageSize)

    const result: PaginatedResult<T> = {
      records,
      totalPages,
      pageSize,
      pages: {
      }
    }

    for (let i = 0; i < totalPages; i++) {
      const prop = (i + 1).toString()
      const start = i * pageSize
      const end = start + pageSize <= records ? start + pageSize : records

      result.pages[prop as keyof PaginatedResult<T>] = docs.slice(start, end)
    }

    return result
  } catch (error) {
    console.error(error)
  }

  return { pages: {} }
}

const getPaginatedByTags = async (type: string, pageSize: number = 12): Promise<Array<PaginatedByTagsResult<Article>>> => {
  try {
    const { docs } = await getApi<Article>(type)

    const allTags = docs.map((doc) => doc.tags).flat()
    const uniqueTags = [...new Map(allTags.map((tag: any) => [tag.id, tag])).values()]

    const result = uniqueTags.map((tag: any) => {
      const filteredDocs = docs.filter((doc: any) => {
        const matchedTag = doc.tags.find((x: any) => x.slug === tag.slug)
        return matchedTag
      })

      const records = filteredDocs.length
      const totalPages = Math.ceil(records / pageSize)

      const _result: PaginatedByTagsResult<Article> = {
        tag,
        records,
        totalPages,
        pageSize,
        pages: {
        }
      }

      for (let i = 0; i < totalPages; i++) {
        const prop = (i + 1).toString()
        const start = i * pageSize
        const end = start + pageSize <= records ? start + pageSize : records

        _result.pages[prop as keyof PaginatedByTagsResult<Article>] = filteredDocs.slice(start, end)
      }

      return _result
    })

    return result
  } catch (error) {
    console.error(error)
  }

  return []
}

export { getApi, getEnumerable, getPaginated, getPaginatedByTags }
